INSERT INTO `wp_postmeta` VALUES (2204,814,'_service_section_0_subtitle','field_6640e106a8471');
INSERT INTO `wp_postmeta` VALUES (2205,814,'service_section_0_description','Für die langfistige Gesunderhaltung Ihrer Augen und Schutz vor frühzeitigen degenertiven Veränderungen ist eine gesunde Ernährung von fundamentaler Bedeutung. In unserem regionalen Saisonkalender erfahren Sie, welches Obst und Gemüse aus einheimischen Anbau Sie gerade ernten oder kaufen können. Dabei schont saisonales Einkaufen die Umwelt durch kürzere Transportwege. Obst und Gemüse aus regionalem Anbau schmeckt zudem meist deutlich besser, ist gesünder und bleibt länger haltbar. Ein bewusster und nachhaltiger Umgang mit der Umwelt liegt uns bei GermanEyeCare besonders am Herzen.');
