INSERT INTO `wp_postmeta` VALUES (343,605,'news_section_0_post_section_title','Der retinale Venenverschluss - ein Praxisfall');
INSERT INTO `wp_postmeta` VALUES (344,605,'_news_section_0_post_section_title','field_6644a2e54a2a3');
