INSERT INTO `wp_postmeta` VALUES (553,628,'news_section_2_post_section_description','Der Patient litt an einem starken Blepharospasmus, der auf eine Fehlinnervation des musculus orbicularis oculi, was auf den nervus facialis zurckzufhren ist. Die besondere symptomatische Konstellation mit der oromandibulren Dystonie, fhrte zur Diagnose des Meige-Syndroms.  ');
