INSERT INTO `wp_postmeta` VALUES (1609,762,'news_section_1_post_section_description','Augenhintergrund (Abb. 1) rechte zentrale Netzhaut\r\n\r\nBei der Fundusfotografie konnten ausschlielich alterstypische  Vernderungen festgestellt werden, die sich innerhalb der Normvarianz befanden.\r\n\r\n<ul>\r\n<li><p>Glaskrper: normal OU</p></li>\r\n<li><p>Papille: normal OU, Cup to Disc Ratio: 0,2 OU</p></li>\r\n<li><p>Makula: normal OU</p></li>\r\n<li><p>Gefe: Arterien-Venen-Verhltnis: 0,5  </p></li>\r\n<li><p>Peripherie: normal OU  </p></li>\r\n</ul>\r\n\r\nOptometrische Messungen: 4/2019');
