INSERT INTO `wp_postmeta` VALUES (1778,784,'news_section_2_post_section_title','Kritische Vernderungen');
INSERT INTO `wp_postmeta` VALUES (1779,784,'_news_section_2_post_section_title','field_6644a2e54a2a3');
INSERT INTO `wp_postmeta` VALUES (1780,784,'news_section_2_post_section_subtitle','');
